$mongo  = $container->get('doctrine.odm.mongodb.document_manager');
$em     = $container->get('doctrine')->getEntityManager('default');
$domain = $em->getRepository('DomainBundle:Client')->findOneBy([ "domain" => $argv[1] ]);

if (!$domain) {
    $output->writeln("Domain not found: {$argv[1]}");
    exit;
}

$mongoClient    = $mongo->getConnection()->getMongoClient();
$clientDatabase = StringHelper::slugDomain($domain->getDomain());
$database       = $mongoClient->$clientDatabase;

updateTerms($database, 'terms_pt_br', $ptBrTerms);
updateTerms($database, 'terms_en', $enTerms);
updateTerms($database, 'terms_es', $esTerms);

function updateTerms($database, $keyToSearch, $updateValue)
{
    $database->configurations->update(
        [ 'items.key' => $keyToSearch ],
        [ '$set' => [ 'items.$.value' => $updateValue ] ],
        [ 'multiple' => true ]
    );
}
